function terminalOutput(message) {
    var outputDiv = document.createElement("div");
    outputDiv.id = "terminal__output";

    var promptSpan = document.createElement("span");
    promptSpan.textContent = "Darshan@ubuntu:";
    promptSpan.style.color = "#7eda28"; // Setting color to green

    var locationSpan = document.createElement("span");
    locationSpan.id = "terminal__prompt--location";
    locationSpan.textContent = "~";

    var blingSpan = document.createElement("span");
    blingSpan.id = "terminal__prompt--bling";
    blingSpan.textContent = "$";

    var outputMessage = document.createElement("span");
    outputMessage.textContent = " " + message; // Add a space before the message

    outputDiv.appendChild(promptSpan);
    outputDiv.appendChild(locationSpan);
    outputDiv.appendChild(blingSpan);
    outputDiv.appendChild(outputMessage);

    document.getElementById("terminal__body").appendChild(outputDiv);
}
